import React from 'react';
import './Modal.css';
import Aux from '../../../hoc/Auxilary'



const Modal = (props)=>{
    return(
        <Aux>
        <div className="Modal" 
            style ={{
                 transform :props.show ? 'translateY(0)' : 'translateY(-100vh)',
                 opacity : props.show ? '1' : '0'
                }}>
            {props.children}
        </div>
        </Aux>
    );
}

export default Modal
