import React from 'react';
import './Input.css';

const Input = (props) => {
    let inputElement = null;
    const inputClasses ='InputElement'
    if (props.invalid){
        inputClasses.push(className="Invalid")
    }
    switch (props.elementType){
        case('input'):
        inputElement = 
            <input className={inputClasses}
            {...props.elementConfig} 
            value={props.value}
            onChange={props.handleChange}/>;
        break;
        case('textArea'):
        inputElement = 
            < textarea className={inputClasses} 
            {...props.elementConfig} 
            value={props.value}
            onChange={props.handleChange}
            />
        break;
        case('select'):
        inputElement = (
                        < select className={inputClasses}
                        onChange={props.handleChange}
                        value={props.value}>
                            {props.elementConfig.options.map(option =>(
                                <option key={option.value}  value={option.value}>
                                    {option.displayValue}
                                </option>
                            ))}
                        </ select>);
        break;
        default:
        inputElement = 
            <input className={inputClasses}
            {...props.elementConfig} 
            value={props.value}/>
    }
    return(
        <div className="Input">
            <label className="Label">{props.label}</label>
            {inputElement}
        </div>
    );
}

export default Input;
