import React from 'react';
import './Input.css';

const Input = (props) => {
    let inputElement = null;
    switch (props.elementType){
        case('input'):
        inputElement = 
        <input className="InputElement"
         {...props.elementConfig} 
         value={props.value}/>;
        break;
        case('textArea'):
        inputElement = 
        < textarea className="InputElement" 
        {...props.elementConfig} 
        value={props.value}/>
        break;
        default:
        inputElement = 
        <input className="InputElement" 
        {...props.elementConfig} 
        value={props.value}/>
    }
    return(
        <div className="Input">
            <label className="Label">{props.label}</label>
            {inputElement}
        </div>
    );
}

export default Input;
